rm.opponentType" btn-radio="item.value">{{item.label}} </button> </div> </div> </div> </div> </div> </div> <div ng-if="form.contestType == \'Head to Head\'"> <div class="panel panel-contest"> <div class="panel-heading">Entry Fee</div> <div class="panel-body"> <!-- TODO: This multi-entry fee functionality removed for now\n              <ul class="entry-fees">\n                <li ng-repeat="item in entryFeeGames">\n                  {{item.gamesToOffer.label}} contest<span ng-if="item.gamesToOffer.value > 1">s</span> @ {{item.entryFee.label}} entry fee\n                  <button type="button" class="btn btn-link" ng-click="entryFeeRemove($index)"><i class="fa fa-times-circle icon-remove"></i></button>\n                </li>\n              </ul>\n              --> <div class="row"> <div class="col-sm-6"> <!--<div class="form-groups-adjacent">--> <div class="form-group"> <div class="input-group"> <select id="entryFeeField1" name="entryFee" class="form-control" ng-options="item.label for item in model.meta.entryFees" ng-model="form.entryFee" ng-required="form.contestType === \'Head to Head\'"> <option value="">Select entry fee </select> </div> </div> <!-- TODO: This multi-entry fee functionality removed for now\n                  <div class="form-group pull-left">\n                    <span class="form-text">create</span>\n                    <div class="input-group pull-left">\n                      <select id="gamesToOfferField" name="gamesToOffer" class="form-control"\n                              ng-options="item.label for item in model.meta.gamesToOffer" ng-model="form.gamesToOffer">\n                        <option value="">Select # games</option>\n                      </select>\n                    </div>\n                    <span class="form-text">games</span>\n                  </div>\n                </div>\n                --> </div> </div> <!-- TODO: This multi-entry fee functionality removed for now\n              <div class="">\n              <button type="button" class="btn btn-link" ng-click="entryFeeAdd()"><i class="fa fa-plus icon-add"></i> Add\n              <span ng-if="entryFeeGames.length > 0"> another</span></button>\n              </div>\n              --> </div> </div> </div> <div ng-if="form.contestType == \'League\'"> <div class="row"> <div class="col-md-6"> <div class="panel panel-contest"> <div class="panel-heading">League Size</div> <div class="panel-body"> <div class="input-group"> <select id="leagueSizeField" name="leagueSize" class="form-control" ng-options="item for item in leagueSizes" ng-model="form.leagueSize" ng-change="updatePrizeStructure(form.leagueSize)" ng-required="form.contestType === \'League\'"> <option value="">Add league size </select> </div> </div> </div> </div> </div> <div class="row"> <div class="col-md-6"> <div class="panel panel-contest"> <div class="panel-heading">Entry Fee & Prize Structure</div> <div class="panel-body"> <div class="buttons"> <!--<div class="form-groups-adjacent">--> <!--<div class="form-group">--> <div class="input-group"> <select id="entryFeeField" name="entryFee" class="form-control" ng-options="item.label for item in model.meta.entryFees" ng-model="form.entryFee" ng-required="form.contestType === \'League\'"> <option value="">Select entry fee </select> </div> <!--</div>--> <div class="form-group"> <div class="input-group"> <select id="prizeTypeField" name="prizeType" class="form-control" ng-disabled="isPrizeTypeDisabled()" ng-options="item.label for item in model.meta.prizeTypes" ng-model="form.prizeType" ng-required="form.contestType === \'League\'"> <option value="">Select prize structure </select> </div> </div> </div> </div> </div> </div> </div> <div class="panel panel-contest"> <div class="panel-heading">Prizes</div> <div class="panel-body"> <ul class="prizes"> <li ng-repeat="prize in prizes track by $index"> <span class="badge">{{$index+1}}</span> {{prize | currency}} </li> </ul> </div> </div> </div> <div class="row"> <div class="col-sm-6 col-md-4"> <div class="form-group"> <label for="contestNameField">Name your contest (optional)</label> <input ng-model="form.contestName" type="text" class="form-control" name="contestName" id="contestNameField" placeholder="" ng-maxlength="40"> <div class="messages" ng-show="createContestForm.contestName.$error.maxlength"> Contest name maximum length is 40 characters.</div> </div> </div> </div> <footer> <div class="row"> <div class="col-sm-6"> </div> <div class="col-sm-6"> <div ng-if="form.contestType == \'Head to Head\'"> <!--<p class="total-fee">Total entry fee: {{ 0 | currency }}</p>--> <p class="potential-winnings">Potential winnings: {{ potentialWinnings() | currency }}</p> </div> </div> </div> <div class="row"> <div class="col-sm-6"> </div> <div class="col-sm-6"> <button type="submit" class="btn btn-default btn-lg pull-right" ng-click="submit()" ng-disabled="$root.memberBlocked || createContestForm.contestName.$error.maxlength"> <span>Select Team</span> </button> </div> </div> </footer> </form> </div> </div> </div>'),a.put("views/enter-contest.html",'<div class="title-bar"> <div class="container"> <h1 class="title">Enter Contest</h1> </div> </div> <div class="container"> <div class="panel"> <div class="panel-body" ng-cloak> <div class="row"> <div class="col-sm-7 col-lg-7"> <h1 class="contest-name">{{ contest.name }}</h1> <ul class="contest-summary"> <li>Entries: <span>{{ contest.entriesTotal }}</span> / <span>{{ contest.size }}</span></li> <li>Entry fee: {{ contest.entryFee.amount | money: contest.entryFee.currency }}</li> <li>Prizes: <span>{{contest.prizePool.amount | money: contest.prizePool.currency }}</span></li> </ul> </div> <div class="col-sm-5 col-lg-5"> <p class="contest-starts" ng-if="!contestExpired.val"> <span class="info">Contest starts {{formatDate(contest.start, \'dddd h:mma z\')}}</span> <countdown ng-if="contest.start" end-time="contest.start" expired="contestExpired"> </p> <p class="contest-started" ng-if="contestExpired.val">Contest has already started!</p> <!--<span class="pull-right">Time remaining: {{timeRemaining}}</span>--> <p class="rules-scoring"><a href rules-modal sport="sport" prizes="contest.prizeAllocations.allocations" scale="false" contest="contest">Rules, Scoring &amp; Prizes</a></p> </div> </div> <div class="row"> <div class="col-xs-5"> <div class="btn-group games" dropdown is-open="status.isopen"> <button type="button" class="btn btn-default dropdown-toggle" dropdown-toggle aria-expanded="false"> <div ng-show="!gameSelected" class="detail">All Games ({{::games.length}})</div> <div ng-show="gameSelected" class="detail"> <div class="name">{{ gameSelected.name }}</div> <div class="date">{{ formatDate(gameSelected.date, \'ddd h:mm a\')}}</div> </div> <span class="icon"><i class="fa" ng-class="{\'fa-angle-down\': !status.isopen, \'fa-angle-up\': status.isopen}"></i></span> </button> <ul class="dropdown-menu" role="menu"> <li class="game" ng-class="{active: !gameSelected }"><a href ng-click="selectGame(null)">All Games ({{::games.length}})</a></li> <li ng-repeat="game in orderedGames = (games | orderBy : \'date\') track by game.id" class="game" ng-class="{active: gameSelected == game }"> <a href ng-click="selectGame(game)"> <div class="name">{{ ::game.name }}</div> <div class="date">{{ formatDate(game.date, \'ddd h:mm a\')}}</div> </a> </li> </ul> </div> </div> <div class="col-xs-7"> <div class="salaries visible-xs-block visible-sm-block"> <div class="salary-remaining" ng-class="{exceeded: salaryRemaining < 0}"> <div class="value"> {{ salaryRemaining | mbCurrency : \'$\' : 0}} </div> <div class="title"> Salary <span ng-if="salaryRemaining >= 0">remaining</span><span ng-if="salaryRemaining < 0">Exceeded!</span> </div> </div> <div class="player-average"> <div class="value"> {{averageSalaryRemaining() | mbCurrency : \'$\' : 0}} </div> <div class="title"> Avg/Player </div> </div> </div> </div> </div> <!--<div class="toggle-search">--> <!--<i ng-click="ctl.showSearch = !ctl.showSearch" class="fa fa-searc